import React, { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [id, setId] = useState('');

  const upload = async () => {
    if (!file) {
      setStatus('Please select a video file');
      return;
    }
    try {
      const fd = new FormData();
      fd.append('video', file);
      const { data } = await axios.post('http://localhost:3001/api/upload', fd);
      setId(data.id);
      setStatus('processing');
      checkStatus(data.id);
    } catch (error) {
      setStatus('Upload failed: ' + (error.response?.data || error.message));
    }
  };

  const checkStatus = async (videoId) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/task/${videoId}`);
      setStatus(data.status);
      if (data.status === 'processing') {
        setTimeout(() => checkStatus(videoId), 2000);
      }
    } catch (error) {
      setStatus('Status check failed: ' + (error.response?.data || error.message));
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div style={{ marginBottom: '25px' }}>
        <label style={{ 
          display: 'block', 
          marginBottom: '10px', 
          fontSize: '16px', 
          fontWeight: 'bold',
          color: '#333'
        }}>Select Video File:</label>
        <input 
          type="file" 
          accept="video/*" 
          onChange={e => setFile(e.target.files[0])}
          style={{
            padding: '10px',
            border: '2px dashed #007bff',
            borderRadius: '4px',
            backgroundColor: '#f8f9fa',
            cursor: 'pointer'
          }}
        />
      </div>
      <button 
        onClick={upload}
        style={{
          padding: '12px 30px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          fontSize: '16px',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
      >
        Upload & Process
      </button>
      {status && (
        <div style={{
          padding: '15px',
          backgroundColor: status === 'done' ? '#d4edda' : status.includes('failed') ? '#f8d7da' : '#fff3cd',
          border: `1px solid ${status === 'done' ? '#c3e6cb' : status.includes('failed') ? '#f5c6cb' : '#ffeaa7'}`,
          borderRadius: '4px',
          marginBottom: '20px',
          fontSize: '16px',
          fontWeight: 'bold'
        }}>
          Status: {status}
        </div>
      )}
      {status === 'done' && (
        <div style={{ marginTop: '20px' }}>
          <h3 style={{ marginBottom: '15px', color: '#333' }}>Processed Video:</h3>
          <video
            src={`http://localhost:3001/api/result/${id}`}
            controls
            width="600"
            style={{
              border: '1px solid #ddd',
              borderRadius: '4px'
            }}
          />
        </div>
      )}
    </div>
  );
}
