import React, { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [id, setId] = useState('');

  const upload = async () => {
    if (!file) {
      setStatus('Please select a video file');
      return;
    }
    try {
      const fd = new FormData();
      fd.append('video', file);
      const { data } = await axios.post('http://localhost:3001/api/upload', fd);
      setId(data.id);
      setStatus('processing');
      checkStatus(data.id);
    } catch (error) {
      setStatus('Upload failed: ' + (error.response?.data || error.message));
    }
  };

  const checkStatus = async (videoId) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/task/${videoId}`);
      setStatus(data.status);
      if (data.status === 'processing') {
        setTimeout(() => checkStatus(videoId), 2000);
      }
    } catch (error) {
      setStatus('Status check failed: ' + (error.response?.data || error.message));
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div style={{ marginBottom: '25px' }}>
        <label style={{ 
          display: 'block', 
          marginBottom: '10px', 
          fontSize: '16px', 
          fontWeight: 'bold',
          color: '#333'
        }}>Select Video File:</label>
        <input 
          type="file" 
          accept="video/*" 
          onChange={e => setFile(e.target.files[0])}
          style={{
            padding: '10px',
            border: '2px dashed #007bff',
            borderRadius: '4px',
            backgroundColor: '#f8f9fa',
            cursor: 'pointer'
          }}
        />
      </div>
      <button 
        onClick={upload}
        style={{
          padding: '12px 30px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          fontSize: '16px',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
      >
        Upload & Process
      </button>
      {status && (
        <div style={{
          padding: '15px',
          backgroundColor: status === 'done' ? '#d4edda' : status.includes('failed') ? '#f8d7da' : '#fff3cd',
          border: `1px solid ${status === 'done' ? '#c3e6cb' : status.includes('failed') ? '#f5c6cb' : '#ffeaa7'}`,
          borderRadius: '4px',
          marginBottom: '20px',
          fontSize: '16px',
          fontWeight: 'bold'
        }}>
          Status: {status}
          {status === 'processing' && (
            <div style={{ marginTop: '10px' }}>
              <div style={{
                width: '100%',
                height: '8px',
                backgroundColor: '#e0e0e0',
                borderRadius: '4px',
                overflow: 'hidden'
              }}>
                <div style={{
                  width: '100%',
                  height: '100%',
                  background: 'linear-gradient(90deg, #007bff 25%, transparent 25%, transparent 50%, #007bff 50%, #007bff 75%, transparent 75%)',
                  backgroundSize: '20px 20px',
                  animation: 'progress 1s linear infinite'
                }}></div>
              </div>
              <style>{`
                @keyframes progress {
                  0% { background-position: 0 0; }
                  100% { background-position: 20px 0; }
                }
              `}</style>
            </div>
          )}
        </div>
      )}
      {status === 'done' && (
        <video
          src={`http://localhost:3001/api/result/${id}`}
          controls
          width="400"
        />
      )}
    </div>
  );
}
