import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const nav = useNavigate();

  const login = async () => {
    await axios.post('http://localhost:3001/api/signin', { email, password: pass });
    nav('/dashboard');
  };

  const soccerBallStyle = {
    width: '60px',
    height: '60px',
    borderRadius: '50%',
    background: 'linear-gradient(45deg, #000 25%, transparent 25%), linear-gradient(-45deg, #000 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #000 75%), linear-gradient(-45deg, transparent 75%, #000 75%)',
    backgroundSize: '20px 20px',
    backgroundPosition: '0 0, 0 10px, 10px -10px, -10px 0px',
    backgroundColor: '#fff',
    border: '3px solid #000',
    margin: '0 auto 20px auto',
    display: 'block'
  };

  return (
    <div style={{ 
      maxWidth: '450px', 
      margin: '50px auto', 
      padding: '50px', 
      borderRadius: '25px',
      backgroundColor: '#fff',
      boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
      border: '2px solid #e3f2fd'
    }}>
      <div style={soccerBallStyle}></div>
      <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#1976d2', fontWeight: 'bold' }}>Football Coach</h2>
      <h3 style={{ textAlign: 'center', marginBottom: '30px', color: '#666', fontSize: '18px' }}>Sign In</h3>
      <div style={{ marginBottom: '20px' }}>
        <input 
          placeholder="Email" 
          onChange={e => setEmail(e.target.value)} 
          style={{
            width: '100%',
            padding: '15px 20px',
            border: '2px solid #e3f2fd',
            borderRadius: '25px',
            fontSize: '16px',
            outline: 'none',
            transition: 'border-color 0.3s'
          }}
        />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="password" 
          placeholder="Password" 
          onChange={e => setPass(e.target.value)} 
          style={{
            width: '100%',
            padding: '15px 20px',
            border: '2px solid #e3f2fd',
            borderRadius: '25px',
            fontSize: '16px',
            outline: 'none',
            transition: 'border-color 0.3s'
          }}
        />
      </div>
      <button 
        onClick={login}
        style={{
          width: '100%',
          padding: '15px',
          backgroundColor: '#1976d2',
          color: 'white',
          border: 'none',
          borderRadius: '25px',
          fontSize: '16px',
          fontWeight: 'bold',
          cursor: 'pointer',
          marginBottom: '20px',
          boxShadow: '0 4px 15px rgba(25, 118, 210, 0.3)'
        }}
      >
        Login
      </button>
      <div style={{ textAlign: 'center' }}>
        <a href="/signup" style={{ color: '#1976d2', textDecoration: 'none', fontWeight: 'bold' }}>Don't have an account? Sign up</a>
      </div>
    </div>
  );
}
