import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Add CSS animation for progress bar
const progressBarStyle = `
  @keyframes progress-slide {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }
  .progress-bar {
    animation: progress-slide 2s ease-in-out infinite;
  }
`;

// Inject styles
function injectStyles() {
  if (!document.getElementById('progress-bar-styles')) {
    const styleSheet = document.createElement('style');
    styleSheet.id = 'progress-bar-styles';
    styleSheet.textContent = progressBarStyle;
    document.head.appendChild(styleSheet);
  }
}

export default function Upload() {
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState('');
  const [id, setId] = useState('');

  useEffect(() => {
    injectStyles();
  }, []);

  const upload = async () => {
    if (!file) {
      setStatus('Please select a video file');
      return;
    }
    try {
      const fd = new FormData();
      fd.append('video', file);
      const { data } = await axios.post('http://localhost:3001/api/upload', fd);
      setId(data.id);
      setStatus('processing');
      checkStatus(data.id);
    } catch (error) {
      setStatus('Upload failed: ' + (error.response?.data || error.message));
    }
  };

  const checkStatus = async (videoId) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/task/${videoId}`);
      setStatus(data.status);
      if (data.status === 'processing') {
        setTimeout(() => checkStatus(videoId), 2000);
      }
    } catch (error) {
      setStatus('Status check failed: ' + (error.response?.data || error.message));
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div style={{ marginBottom: '25px' }}>
        <label style={{ 
          display: 'block', 
          marginBottom: '10px', 
          fontSize: '16px', 
          fontWeight: 'bold',
          color: '#333'
        }}>Select Video File:</label>
        <input 
          type="file" 
          accept="video/*" 
          onChange={e => setFile(e.target.files[0])}
          style={{
            padding: '10px',
            border: '2px dashed #007bff',
            borderRadius: '4px',
            backgroundColor: '#f8f9fa',
            cursor: 'pointer'
          }}
        />
      </div>
      <button 
        onClick={upload}
        style={{
          padding: '12px 30px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          fontSize: '16px',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
      >
        Upload & Process
      </button>
      {status === 'processing' && (
        <div style={{ marginTop: '10px' }}>
          <div style={{ color: 'orange', marginBottom: '10px' }}>
            Processing video... Please wait.
          </div>
          <div style={{ 
            width: '100%', 
            backgroundColor: '#f0f0f0', 
            borderRadius: '10px', 
            overflow: 'hidden',
            height: '20px'
          }}>
            <div 
              className="progress-bar"
              style={{
                width: '100%',
                height: '100%',
                background: 'linear-gradient(90deg, #ff6b35, #f7931e, #ff6b35)',
                backgroundSize: '200% 100%'
              }}
            ></div>
          </div>
        </div>
      )}
      {status && (
        <div style={{
          padding: '15px',
          backgroundColor: status === 'done' ? '#d4edda' : status.includes('failed') ? '#f8d7da' : '#fff3cd',
          border: `1px solid ${status === 'done' ? '#c3e6cb' : status.includes('failed') ? '#f5c6cb' : '#ffeaa7'}`,
          borderRadius: '4px',
          marginBottom: '20px',
          fontSize: '16px',
          fontWeight: 'bold'
        }}>
          Status: {status}
        </div>
      )}
      {status === 'done' && (
        <video
          src={`http://localhost:3001/api/result/${id}`}
          controls
          width="400"
        />
      )}
    </div>
  );
}
