import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function SignIn({ onLogin }) {
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const nav = useNavigate();

  const login = async () => {
    try {
      await axios.post('http://localhost:3001/api/signin', { email, password: pass });
      onLogin();
    } catch (error) {
      const errorMsg = error.response?.data?.error || error.response?.data?.message || error.message || 'Unknown error';
      alert('Login failed: ' + errorMsg);
    }
  };

  return (
    <div style={{ 
      maxWidth: '400px', 
      margin: '50px auto', 
      padding: '40px', 
      border: '1px solid #ddd', 
      borderRadius: '8px',
      backgroundColor: '#fafafa'
    }}>
      <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#333' }}>Sign In</h2>
      <div style={{ marginBottom: '20px' }}>
        <input 
          placeholder="Email" 
          onChange={e => setEmail(e.target.value)} 
          style={{
            width: '100%',
            padding: '12px',
            border: '1px solid #ccc',
            borderRadius: '4px',
            fontSize: '16px'
          }}
        />
      </div>
      <div style={{ marginBottom: '20px' }}>
        <input 
          type="password" 
          placeholder="Password" 
          onChange={e => setPass(e.target.value)} 
          style={{
            width: '100%',
            padding: '12px',
            border: '1px solid #ccc',
            borderRadius: '4px',
            fontSize: '16px'
          }}
        />
      </div>
      <button 
        onClick={login}
        style={{
          width: '100%',
          padding: '12px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          fontSize: '16px',
          cursor: 'pointer',
          marginBottom: '15px'
        }}
      >
        Login
      </button>
      <div style={{ textAlign: 'center' }}>
        <a href="/signup" style={{ color: '#007bff', textDecoration: 'none' }}>Don't have an account? Sign up</a>
      </div>
    </div>
  );
}
